import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  openNav() {
    document.getElementById('mySidenav').style.width = '250px';
    document.getElementById('main').style.marginLeft = '250px';
    document.getElementById('main').style.marginRight = '-250px';
  }
  closeNav() {
    document.getElementById('mySidenav').style.width = '0';
    document.getElementById('main').style.marginLeft = '0';
    document.getElementById('main').style.marginRight = '0';
  }
}
<div id="mySidenav" class="sidenav">
  <a href="javascript:void(0)" class="closebtn" (click)="closeNav()"
    >&#9776;
  </a>
  <a href="#">Tarot Game 1</a>
  <a href="#">Rummy Game 1</a>
  <a href="#">Tarot Game 2</a>
  <a href="#">Lustik Game 1</a>
</div>

<div id="main">
  <h1>Card calculator</h1>
  <div id="text">Choose a card game</div>
  <a routerLink="/app-tarot">Tarot</a>
  <br />
  <a href="">French Tarot</a>
  <br />
  <a href="">Rummy</a>
  <br />
  <a href="">Poker</a>
  <br />
  <a href="">Lustik</a>
  <span class="nav" (click)="openNav()">&#9776;</span>
</div>
